  margin: 0;
  padding: 0;
  box-sizing: border-box;
  outline: none;
}

ul,
li {
  list-style: none;
  padding-left: 0;
  margin: 0;
}

p {
  margin: 0;
}

h1,
h2,
h3,
h4,
h5 {
  margin: 0;
}

img {
  display: block;
  max-width: 100%;
}

a {
  display: block;
  text-decoration: none;
  color: #fff;
  cursor: pointer;

  &:hover {
    color: #fff;
  }
}

button {
  border: none;
  cursor: pointer;
}

body {
  background: map-get($theme, "background");
  color: map-get($theme, "text");
  font:
    400 16px "Noto Sans",
    sans-serif;
}

html {
  font-size: 16px;

  @media (max-width: 1600px) {
    font-size: 14px;
  }

  @media (max-width: 560px) {
    font-size: 16px;
  }
}

.container-fluid {
  width: 100%;
  padding: 0 4.6875rem;

  @media (max-width: 1600px) {
    padding: 0 3rem;
  }

  @media (max-width: 1366px) {
    padding: 0 2rem;
  }

  @media (max-width: 560px) {
    padding: 0 1rem;
  }
}

.container {
  width: 100%;
  max-width: 1251px;
  padding: 0 15px;
  margin: 0 auto;

  @media (max-width: 560px) {
    padding: 0 1rem;
  }
}

